mapper.select:
with selected_articles as ( select articles.id as id, articles.slug as slug, articles.title as title, articles.description as description, articles.body as body, articles.created_at as createdAt, articles.updated_at as updatedAt, users.id as author_id, users.username as author_username, (follower_id is not null) as author_following from articles inner join users on articles.user_id = users.id left join follows on users.id = follows.followee_id and follows.follower_id = #{currentUser}
articles.slug = #{slug}
articles.id = #{id}
), currentUserFavorites as ( select article_id from article_favorites where user_id = ${currentUser})
select sa.*, count(af.user_id) as favorites_count, (sa.id in (select * from currentUserFavorites)) as favorited from selected_articles sa left join article_favorites af on sa.id = af.article_id group by sa.id
